mapper.select:
select distinct p.id, p.menu_name, p.parent_id, p.order_num, p.path, p.component, p.query, p.route_name, p.is_frame, p.frame_url, p.is_cache, p.menu_type, p.visible, p.status, p.perms, p.icon, p.create_by, p.create_time, p.create_user_name, p.update_by, p.update_time, p.update_user_name, p.remark, p.is_leaf, p.always_show, p.color, p.default_shortcut_menu, p.reminder_menu, p.tenant_menu, p.allow_visitor_access from smart_permission p left join smart_role_permission rp on p.id = rp.permission_id left join smart_role r on rp.role_id = r.id left join smart_user_role ur on r.id = ur.role_id left join smart_user u on ur.user_id = u.id
and p.visible = #{visible}
and p.id = #{id}
and u.id = #{userId}
and p.status = #{status}
and p.menu_name like concat('%', #{menuName}, '%')
and p.parent_id = #{parentId}
and p.menu_type = #{menuType}
and p.menu_type in ('M', 'C')
order by p.order_num asc
